mapper.update:
update tbl_payment p join tbl_member m on p.member_id = m.id
set m.member_grade = 'subscribe'
where m.id = #{memberId};

mapper.select:
select id, created_date, updated_date, payment_start_date, payment_update_date, subscribe_id, member_id
from tbl_payment
where member_id = #{memberId} and subscribe_id = #{subscribeId}
